mapper.insert:
INSERT INTO member( id, pw, name, nickname, phone, email, recommender
)VALUES( #{id}, #{pw}, #{name}, #{nickname}, #{phone}, #{email}, #{recommender}
)

mapper.update:
UPDATE member
SET point=point + #{point}
WHERE member_index=#{member_index}

UPDATE member SET
del_yn = 'Y', del_date = NOW(), del_reason = #{del_reason}
WHERE member_index = #{member_index}

UPDATE member SET
name = #{name}, phone = #{phone}, nickname = #{nickname}
WHERE member_index = #{member_index}

mapper.select:
SELECT pw, member_index 
FROM member 
WHERE member_index = #{member_index}

mapper.update:
UPDATE MEMBER SET
pw = #{pw}
WHERE member_index = #{member_index}

UPDATE MEMBER SET
address = #{address}
WHERE member_index = #{member_index}

UPDATE member SET
del_yn = 'Y', del_date = NOW(), del_reason = '관리자에 의한 추방'
WHERE member_index = #{member_index}

mapper.select:
SELECT event_ticket
FROM member
WHERE member_index = #{member_index}

mapper.update:
UPDATE member SET
event_ticket = event_ticket - 1
WHERE member_index = #{member_index}
