mapper.insert:
INSERT INTO member( id, pw, name, nickname, phone, email, recommender
)VALUES( #{id}, #{pw}, #{name}, #{nickname}, #{phone}, #{email}, #{recommender}
)

mapper.update:
UPDATE member
SET point=point + #{point}
WHERE member_index=#{member_index}

mapper.select:
SELECT member_index, id, nickname, name, join_date, del_date, del_reason FROM member WHERE del_yn = #{del_yn}
AND id LIKE CONCAT('%',#{searchValue},'%')
AND name LIKE CONCAT('%',#{searchValue},'%')
AND nickname LIKE CONCAT('%',#{searchValue},'%')
ORDER BY join_date DESC LIMIT #{start}, #{perPage}

mapper.update:
UPDATE member SET
del_yn = 'Y', del_date = NOW(), del_reason = '관리자에 의한 추방'
WHERE member_index = #{member_index}
