mapper.insert:
INSERT INTO serviceCenter(
	member_index, sort1, sort2, title, contents
)VALUES(
	#{member_index}, #{sort1}, #{sort2}, #{title}, #{contents}
)

mapper.update:
UPDATE serviceCenter SET
progress = '처리완료'
WHERE serviceCenter_index = #{serviceCenter_index}

UPDATE serviceCenter SET
del_YN = 'Y'
WHERE serviceCenter_index = #{serviceCenter_index}
